mapper.select:
    SELECT 

    FROM t_channel 
	WHERE 1=1

       AND FULL_NAME LIKE  "%"#{channel.fullName}"%"


       AND TEL = #{channel.tel}


       AND API_KEY = #{channel.apiKey}


       AND OP_STATUS = #{channel.opStatus}



    	AND CHANNEL_ID in

  		#{channelId}



    	AND CHANNEL_ID IS NULL


	   LIMIT #{start},#{end}

    SELECT count(*) FROM t_channel 
	WHERE 1=1

       AND FULL_NAME LIKE  "%"#{channel.fullName}"%"


       AND TEL = #{channel.tel}


       AND API_KEY = #{channel.apiKey}


       AND OP_STATUS = #{channel.opStatus}



    	AND CHANNEL_ID in

  		#{channelId}



    	AND CHANNEL_ID IS NULL
